"import scala.collection.mutable.ListBuffer\n\nobject MainObject{\n  def main(args:Array[String]){\n    print(\"Hello Scala\")\n\n    var (a,b,c,d) = (0,0,0,0)\n    var ys = List(\"s1\",\"ss\",\"s3\")\n    var ans =ListBuffer[Char]()\n    var xs = List(1,3,4,5,6,7)\n    var x = 5\n    var index= -1\n    var i=0\n    var result = ListBuffer[Int]()\n\n\n    if(a==100) println(\"aaa\")\n    else if(a==110 || b==110) println(\"eee\")\n    else if(b==200 && c<a) println(\"bbb\")\n    else if(b==200 && c==200) println(\"ddd\")\n    else println(\"ccc\")\n\n\n    if(a==100){\n      val d =1\n      println(\"100\")\n    }\n    else if(a==200){\n      val d=2\n      println(\"2000\")\n    }\n    else {\n      val d=3\n      println(\"none\")\n    }\n\n    //\n    for (x <- xs){\n      if(x==1)              result += f(x+1)\n      else if(x==2) result += f(x+2)\n      else if(x==3) result += f(x+3)\n      else result += f(x+4)\n    }\n\n    if(a>c) 0\n    else if (a<c && a>d) 1\n    else if (a==d) 2\n    else if (a<d) 3\n    else if (a==500) 4\n    else 5\n\n   for (x <- xs) {\n        if(h(x))\n            println(x)\n   }\n\n   for(i <- xs.indices){\n     if(xs(i)==x)\n       index=i\n   }\n\n\n   def h(x: Int):Boolean={\n     x%2==0\n   }\n\n   def loop11(xs: List[Int]):List[Int] ={\n     var list = ListBuffer[Int]()\n     var i =0\n     while (h(xs(i))) {\n       i += 1\n     }\n     while(i!=xs.length){\n       list += xs(i)\n       i += 1\n     }\n     list.toList\n   }\n\n    def f(x:Int):Int = {\n      x+3\n    }\n\n   for(y <- ys){\n     for(e <- y){\n       ans += e\n     }\n   }\n\n   while(index<xs.length && !h(xs(index))){\n     index = index+1\n   }\n\n\n\n\n  }\n}"